function decreaseMars() {
  let ticketsMars = document.getElementById("ticketsMars");
  if (ticketsMars.value != 0) {
    ticketsMars.value = parseInt(ticketsMars.value) - 1;
  } else {
    ticketsMars.value = 0;
  }
}

function increaseMars() {
  let ticketsMars = document.getElementById("ticketsMars");
  ticketsMars.value = parseInt(ticketsMars.value) + 1;
}

